          for (let j = 0; j < this.branchMultiple.length; j++) if (this.userMultiple[i].branchName == this.branchMultiple[j].branch) {
            temp = true;
            break;
          } else temp = false;
        }
        if (temp) this.isBranchChanged = false;else this.isBranchChanged = true;
      }
