      var uniqueItems = [];
      filteredItems.forEach(item => {
        var existingItem = uniqueItems.find(uniqueItem => uniqueItem.code === item.code);
        if (existingItem) {
          existingItem.quantity += item.quantity;
        } else {
          uniqueItems.push({
            ...item
          });
        }
      });
      this.newTransaction.items = this.newTransaction.items.filter(item => item.code !== uniqueItems[0].code || item.boxNumber !== uniqueItems[0].boxNumber);
      this.newTransaction.items.push(uniqueItems[0]);
